function burgerMenu(){const e=document.querySelector(".burger"),c=document.querySelector(".header__navigation"),t=document.querySelector(".overlay");e.addEventListener("click",()=>{e.classList.toggle("active"),c.classList.toggle("active"),t.classList.toggle("active"),document.body.classList.add("scroll-lock")}),t.addEventListener("click",()=>{e.classList.remove("active"),c.classList.remove("active"),t.classList.remove("active"),document.body.classList.remove("scroll-lock")})}burgerMenu();